<?php//// Конттроллер страниц.//class C_Logs extends C_Admin_Base{	public function before(){		parent::before();				if(!M_Users::Instance()->Can('ALL'))			$this->p404();	}		public function action_index(){		$this->action_page();	}		public function action_page(){		$this->title .= '::Логи';			$mUsers = M_Users::Instance();		$page = isset($this->params[2]) ? (int)$this->params[2] : 1;		$pLogs = new FilePaginaion(LOG_FILE_PATH, 5);			$pLogs->url('/logs/page')->num($page);				$logs = $pLogs->get();				/* stupied! remake! */		foreach($logs as $k => $l)			$logs[$k]['user'] = $mUsers->get($l['id_user']);				$this->content = $this->Template('logs/v_all.php',  array('logs' => $logs,									'navbar' => $this->Template('v_navbar.php', $pLogs->navbar())));	}}