<?php//// Конттроллер корзины//class C_Trash extends C_Admin_Base{	private $_model; // класс модели удаленного объекта	public function action_mode()	{		$this->title = $this->params[1].':: Удалённые объекты';		$mname = 'M_'.ucfirst($this->params[1]);		$pkname = 'id_' . substr($this->params[1], 0, -1);			if (class_exists($mname ))			$this->_model = call_user_func(array($mname, 'Instance'));		else return false;		$table = $this->_model->get_table();		$page_num = isset($this->params[2]) ? (int)$this->params[2] : 1;  		// создание объекта постраничной навигации (обязательные параметры)		$mPagination = new Pagination($table, "trash/$table/");		// задание свойств объекта		$lang = I18n::language();		$trash_pages = $mPagination->on_page(10)->page_num($page_num)->left_join("i18n using($pkname)")								   ->where("i18n.is_show = '2' AND id_lang = '$lang'")->page();		  		// получение html-кода панели навигации		$navbar = $this->Template('v_navbar.php', array('navparams' => $mPagination->navparams()));		 // формирование контента страницы  		$this->content = $this->Template('trash/v_trash_pages.php',   			array('table'=>	$table,  				'pk' => $this->_model->get_pk(),  				'trash_pages' => $trash_pages,                'navparams' => $mPagination->navparams(),                'navbar' => $navbar));	}	}