<?phpclass M_Templates extends Model{	private static $instance;	// экземпляр класса	public static function Instance()	{		if (self::$instance == null)			self::$instance = new self();					return self::$instance;	}		public function __construct(){		parent::__construct('templates', 'id_template');	}		public function get_templates($type){		$files = array();		$dir = ($type == 1) ? 'inner_templates' : 'base_templates';		$scan = scandir('v/themes/' . CURENT_THEME . "/$dir");		foreach($scan as $file)		{		    if (!is_dir("v/$file"))		    {		        $files[] = $file;		    }		}		return $files;	}}