<?php extract($navparams); ?><div>	<h2>Список новостей (<?=$count?>)</h2>	<table class="table table-hover table-bordered">	<thead>	<tr>		<th class="numberlist">№</th>		<th> Заголовок</th>		<th> Просмотреть</th>		<th> Редактировать</th>		<th> Переместить в корзину</th>		<th> Удалить</th>		<th> Статус</th>	</tr>	</thead>	<tbody>		<?php 	$i = ($page_num - 1) * $on_page + 1; ?>		<?php foreach ($articles as $article): ?>			<tr <?php if($article['is_show'] == 0) echo 'class="no_public_tr"'; echo 'title="Не выводится на данной языковой версии"'; ?> >			<?php $id=$article['id_article'] ?>				<td><?=$i?></td>				<td><?=$article['title'] ?></td>				<td><a href="/news/post/<?=$article['id_article'] ?>" target="_blanc"> Просмотреть</a></td>				<td><a href="/articles/edit/<?=$id ?>"> Редактировать</a></td>                <td><?php if($article['is_show']==0 || $article['is_show']==1): ;?>                     <a href="/articles/remove/<?=$id; ?>"> Переместить в корзину</a>                     <?php endif; ?>                </td>				<?php if(Helpers::can_look('ALL')): ?>				<td><a href="/articles/delete/<?=$id ?>" onClick="javascript: return confirm('Вы действительно хотите удалить?  Данное действие удалит статью со всех языковых версий!')"> Удалить</a></td>				<?php endif; ?>				<td>                 <?php if ($article['is_show'] == 1)                     echo "Опубликована";                   else if($article['is_show'] == 0)                    echo "Не опубликована";                   else if($article['is_show'] == 2)                     echo "Перемещена в корзину";                ?>				</td>			</tr>		<?php $i++; endforeach ?>	</tbody>	</table>	<?=$navbar ?>	<br/>	<p><a class="btn btn-primary btn" href="/articles/add/">Добавить новую статью &raquo;</a></p></div>