<h2>Логи</h2><table class="table table-hover">	<thead>		<tr>			<th class="numberlist">№</th>			<th>Дата</th>			<th>Пользователь</th>			<th>Действие</th>			<th>Ip</th>		</tr>	</thead>	<tbody>		<? 	$i = 1;			foreach($logs as $log): ?>			<tr>				<th><?=$i?></th>				<th><?=$log['dt']?></th>				<th><a href="/users/edit/<?=$log['user']['id_user']?>" target="_blank"><?=$log['user']['name']?></a></th>				<th>					<?=Log::assoc($log['table'], $log['action'])?>					<? if($log['action'] != 'delete'): ?>						<a href="<?=$log['table'] . '/edit/' . $log['id_note']?>">№<?=$log['id_note']?></a>					<? else: ?>						№<?=$log['id_note']?>					<? endif; ?>				</th>				<th><?=$log['ip']?></th>			</tr>		<? $i++; endforeach ?>	</tbody></table><?=$navbar ?>