<?php extract($navparams); ?><div>	<h2>Список текстов (<?=$count?>)</h2>	<table class="table table-hover table-bordered">	<thead>	<tr>		<th class="numberlist">Алиас</th>		<th> Название</th>		<th> Редактировать</th>		<th> Переместить в корзину</th>		<?php if(Helpers::can_look('ALL')): ?>			<th> Удалить</th>		<?php endif; ?>	</tr>	</thead>	<tbody>		<?php foreach ($texts as $text): ?>			<tr>			<?php $id=$text['id_text'] ?>				<td><?=$text['alias'] ?></td>				<td><?=$text['title'] ?></td>				<td><a href="/texts/edit/<?=$id ?>"> Редактировать</a></td>                <td><?php if($text['is_show']==0 || $text['is_show']==1): ;?>                     <a href="/texts/remove/<?=$id; ?>"> Переместить в корзину</a>                     <?php endif; ?>                </td>				<?php if(Helpers::can_look('ALL')): ?>					<td><a href="/texts/delete/<?=$id ?>" onClick="javascript: return confirm('Вы действительно хотите удалить?  Данное действие удалит текст со всех языковых версий!')"> Удалить</a></td>				<?php endif; ?>			</tr>		<?php endforeach ?>	</tbody>	</table>	<?=$navbar ?>	<br/>	<p><a class="btn btn-primary btn" href="/texts/add/">Добавить новый текст &raquo;</a></p></div>