<!DOCTYPE html><html class="no-js" lang="ru">  <head>	<base href="{{C.BASE_URL}}" />	<title>{{ title }}</title>	<meta charset="utf-8" />	{% for style in core_styles %}		<link rel="stylesheet" type="text/css" media="screen" href="/{{C.CSS_DIR}}{{style}}.css" /> 		{% endfor %}	{% for script in core_scripts %}		<script src="/{{C.JS_DIR}}{{script}}.js"></script> 		{% endfor %}	{% for style in styles %}		<link rel="stylesheet" type="text/css" media="screen" href="/v/themes/{{C.CURENT_THEME}}/media/css/{{style}}.css" /> 		{% endfor %}	{% for script in scripts %}		<script src="/v/themes/{{C.CURENT_THEME}}/media/js/{{script}}.js"></script> 		{% endfor %}			<meta name="keywords" content="{{ keywords }}">	<meta name="description" content="{{ description }}">	</head>  <body>        <section class="wrapper">    {% if adminlink %}		<ul id="admin_left">			<li><a href="/pages">Админка</a></li>			<li><a href="/login">Выйти</a></li>		</ul>	{% endif %}      <header class="header">                <div class="header__logo"><a href="/">DofC</a></div>        <div class="header__contacts">          {{ S.Helpers.text('h_contacts') }}        </div>        <div class="header__title">        	 <h2>{{ S.Helpers.text('h_title') }}</h2><br>        	{{ S.Helpers.text('h_subtitle') }}        </div>        <div class="header__line">			<ul>				{% for item in top_menu %}					<li {% if item.is_active %} class="active expanded" {% endif %} 						{{ S.Helpers.front_widget('page', item) }}					>						<a href="/{{ item.full_cache_url }}">							<span {{ S.Helpers.front_field('title_in_menu', 'input') }}> {{ item.title_in_menu }} </span>						</a> 					</li>				{% endfor %}			</ul>          <form class="header__search" action="search" method="post">            <input type="text" name="search" placeholder="Поиск" />            <button type="submit">Найти</button>          </form>        </div>              </header>      <section class="main">        <section class="content">       		{{ content }}    		</section>		<section class="sidebar">                    <nav class="sidebar__menu">            <ul class="sidebar__menu-list">				{% for item in left_menu %}				  <li {% if item.is_active %} class="active expanded" {% endif %} 						{{ S.Helpers.front_widget('page', item) }}				  >				<a href="/{{ item.full_cache_url }}">					<span {{ S.Helpers.front_field('title_in_menu', 'input') }}> {{ item.title_in_menu }} </span>				</a>  				 {% if item.children|length > 0 %}				  <ul>					  {% for i in item.children %}						<li {% if i.is_active %} class="active" {% endif %} 							{{ S.Helpers.front_widget('page', i) }}						>						<a href="/{{ i.full_cache_url }}">							<span {{ S.Helpers.front_field('title_in_menu', 'input') }}> {{ i.title_in_menu }} </span>						</a>					   </li>					  {% endfor %}				  </ul>				 {% endif %}				   </li>				 {% endfor %}            </ul>          </nav>         	</section>              </section>      <footer class="footer">		<div class="footer__contacts">			{{ S.Helpers.text('f_contacts') }}        </div>                <div class="footer__copyright">        	{{ S.Helpers.text('f_copyright') }}        </div>                <div class="footer__developer">        	{{ S.Helpers.text('f_developer') }}        </div>      </footer></body></html>